{
  "report_by_subjects": [
    {
      "id": 1,
      "name": "Matématica",
      "average_calculation": "((9.6 + 9.3)*0.4 + (9.3 + 9.8)*0.6)/2",
      "average_formula": "( ({1º Bimestre} + {2º Bimestre})*0.4 + ({3º Bimestre} + {4º Bimestre})*0.6 )/2",
      "average_year": 8.2,
      "absences": 10,
      "school_calendar_phases": [
        {
          "id": 1,
          "average": 7.2,
          "average_calculation": "(6.5 + 5.7)/2",
          "average_f ormula": "({Nota 1.1} + {Nota 1.2})/2",
          "absences": 2,
          "student_grades": [
            {
              "id": 1,
              "grade": 7.5,
              "student_id": 1,
              "subject_id": 1,
              "assessment_id": 1,
              "school_class_id": 1
            }
          ]
        }
      ]
    }
  ],
  "school_calendar_phases": [
    {
      "id": 1,
      "name": "1º Bimestre",
      "start": "2016-02-01",
      "end": "2016-04-01",
      "school_calendar_id": "1"
    }
  ]
}

